var speRecord = new GlideRecord('x_inhe_spem_spem_approval_matrix');
speRecord.addQuery('exception_type', current.exception_type);
speRecord.query();
if (speRecord.next()) {
	var currentDate = new GlideDateTime();
	var expireDays = parseInt(speRecord.getValue('expiration'));
	currentDate.addDaysLocalTime(expireDays);
	current.expiration_date = currentDate.getDate();
}

