answer = ifScript();
function ifScript() {
	var approvers = new x_inhe_spem.SPEMUtils().getApprovers(current.exception_type, '3',current);
	if (approvers.length != 0) {
		for (var i=0; i<approvers.length; i++) {
			var approval = new GlideRecord('sysapproval_approver');
			approval.addQuery('approver', approvers[0]);
			approval.addQuery('document_id', current.sys_id + '');
			approval.addQuery('state', 'approved');
			if (approval.next()) {
				current.work_notes = 'Skipping Level 3 approval since ' + approval.approver.getDisplayValue() + ' has already approved the request earlier.';
				return 'no';
			}
		}
		return 'yes';
	}
	return 'no';
}

